import { Oval } from 'react-loader-spinner';
import React, { useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFrown } from '@fortawesome/free-solid-svg-icons';
import './App.css';

function GetWeather() {
    const [input, setInput] = useState('');
    const [weather, setWeather] = useState({
        loading: false,
        data: {},
        error: false,
    });

    const currentDateFunction = () => {
        const months = [
            'January', 'February', 'March', 'April', 'May', 'June',
            'July', 'August', 'September', 'October', 'November', 'December',
        ];
        const weekdays = [
            'Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday',
        ];
        const currentDate = new Date();
        const date = `${weekdays[currentDate.getDay()]}, ${months[currentDate.getMonth()]} ${currentDate.getDate()}`;
        return date;
    };

    const fetchWeather = async (event) => {
        if (event.key === 'Enter') {
            event.preventDefault();
            setWeather({ ...weather, loading: true });
            const url = 'https://api.openweathermap.org/data/2.5/weather';
            const api_key = '78f20d8fa7f32545d1f27b4c3048848b';

            try {
                const res = await axios.get(url, {
                    params: {
                        q: input,
                        units: 'imperial',
                        appid: api_key,
                    },
                });
                setWeather({ data: res.data, loading: false, error: false });
            } catch (error) {
                console.error('Error fetching weather:', error);
                setWeather({ data: {}, loading: false, error: true });
            }

            setInput('');
        }
    };

    return (
        <div className="App">
            <h1 className="app-name">Weather App</h1>
            <div className="search-bar">
                <input
                    type="text"
                    className="city-search"
                    placeholder="Enter city name"
                    name="query"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={fetchWeather}
                />
            </div>

            {weather.loading && (
                <div className="Loader">
                    <Oval color="black" height={100} width={100} />
                </div>
            )}

            {weather.error && (
                <span className="error-message">
                    <FontAwesomeIcon icon={faFrown} /> City not found
                </span>
            )}

            {weather.data && weather.data.main && (
                <div>
                    <div className="city-name">
                        <h2>
                            {weather.data.name}, <span>{weather.data.sys.country}</span>
                        </h2>
                    </div>
                    <div className="date">
                        <span>{currentDateFunction()}</span>
                    </div>
                    <div className="icon-temp">
                        <img
                            src={`https://openweathermap.org/img/wn/${weather.data.weather[0].icon}@2x.png`}
                            alt={weather.data.weather[0].description}
                        />
                        {Math.round(weather.data.main.temp)}<sup className="deg">ï¿½F</sup>
                    </div>
                    <div className="des-wind">
                        <p>{weather.data.weather[0].description}</p>
                        <p>Wind Speed: {weather.data.wind.speed} m/s</p>
                    </div>
                </div>
            )}
        </div>
    );
}

export default GetWeather;
